import React, { useState } from 'react';
import axios from 'axios';

function App() {

    const [data, setMsg] = useState(null);

    const onClick = () => {
      const text = document.getElementById("text").value;
      const lang = document.getElementById("lang").value;

      axios.get('/translator/test', {  //비동기통신
             params : {
              msg : text,
              lang : lang 
             }        
      }).then((response) => {setMsg(response.data.message.result.translatedText)
      }).catch(error=>{
        console.log(error);
        throw new Error(error);
      });
    };
    /*자바에서만 받는 태스임, select는 옵션 파티로 같이 */
    return (
        <div>           
          <input type="text" placeholder="한글 문장을 입력해 주세요" id="msg"
          /><br/>
         <select id ="lang">
            <option>----언어선택----</option>
            <option value='en'>영어</option>
            <option value='jp'>일본어</option>

         </select><br/>

          <textarea type="text" placeholder="번역후" id="msg" value={data} /> <br/>


          <button onClick={onClick}>번역하기</button>


      </div>
    );
}

export default App;
