import React, { useState } from 'react';
import { Navigate } from 'react-router-dom';
import App from './App';
//import {BrowserRouter, Link, Routes, route} from 'react-router-dom';

function Login() {
  
    
   

    const onClick2 = () =>{
    const id = document.getElementById('id').value;
    const password = document.getElementById('password').value;
        // axios({ 
        //     method : 'get',
        //     url : '/Login', 
        //          params : {
        //           id : id,
        //           password : password
        //          }
        //   }).then((res) => {
        //     alert("로그인");
        //     window.location.replace('/App');
        //   });
       fetch("/Login",{
         method : 'get',
         body : ({
            id : id,
            password : password,
         }),
       })
       .then(res => res.json())
       .then((data) => {
            console.log(data)
            if(data.token) {Navigate(App)}
            else {alert("ID, PASSWORD 확인")}
       });           
    };
   
   
    return (
    <div>
        <input type='text' placeholder='아이디' id='id'></input>
        <input type='text' placeholder='비밀번호' id='password'></input>
        <button onClick={onClick2}>로그인</button>
    </div>
    );
}

export default Login;
