import React, { useState } from 'react';
import axios from 'axios';

function App() {

    const [data, setData] = useState(null);

    const onClick = () => {
      axios
       .get('/translator/test')
       .then((response) => {setData(response.data)
      });
    };
    return (
      <div>
        <Form>
          <p><textarea name='text'placeholder='번역할 글자 입력'></textarea></p>
        </Form>
        <div>
          <button onClick={onClick}>번역하기</button>
        </div>
        {data && (
        <textarea
        rows={7}
        value={data.message.result.translatedText}
        readOnly={true}
        />
        )}
      </div>
    );
}

export default App;
