import React, { useState } from 'react';
import axios from 'axios';

function App() {

  const text = document.getElementById("text");

    const [data, setData] = useState(text);
    const onClick = () => {
      axios({
        method:"POST",
        url: 'http://localhost:8080/translator/test',
        data:{
            "text": text.value
            }
      })
       .get('/translator/test')
       .then((response) => {setData(response.data)
      });
    };
    return (
        <article>
          <form>
          <p><input type="text" name='title' placeholder="번역할 글자 입력" id="text"></input></p>
          </form>
        <div>
          <button onClick={onClick}>번역하기</button>
        </div>
        {data && (
        <textarea
        rows={7}
        value={data.message.result.translatedText}
        readOnly={true}
        />
        )}
      </article>
    );
}

export default App;
