import React, { Component } from 'react';
import axios from 'axios';
import { render } from '@testing-library/react';

class App extends useState {
  render(){
    const [data, setData] = useState(null);

    const onClick = () => {
      axios
       .get('/translator/test')
       .then((response) => {setData(response.data)
      });
    };
    return (
      <div>

        <div>
          <button onClick={onClick}>번역하기</button>
        </div>
        <div>
            <input type="word" placeholder="번역할 글자를 입력해 주세요." id="word" value="" ></input>
        </div>
        {data && (
        <textarea
        rows={7}
        value={data.message.result.translatedText}
        readOnly={true}
        />
        )}
      </div>
    );
  }

}

export default App;
