import React, { useState } from 'react';
import axios from 'axios';

function App() {

    const text = document.getElementById("text");
    const [data, setData] = useState(text);
    const onClick = () => {
      axios({

                url: '/translator/test',
                method:'put',
                data:{
                    text: text.value
                }
      }).then((response) => {setData(response.data)
      }).catch(error=>{
        console.log(error);
        throw new Error(error);
      });
    };
    return (
        <article>
          <form>
          <p><input type="text" name='title' placeholder="한글 문장을 입력해 주세요" id="text"></input></p>
          </form>
        <div>
          <button onClick={onClick}>번역하기</button>
        </div>
        {data && (
        <textarea
        rows={7}
        value={data.message.result.translatedText}
        readOnly={true}
        />
        )}
      </article>
    );
}

export default App;
