import React, { useState } from 'react';
import axios from 'axios';

function App() {


    const [data, setData] = useState(text);

    const text = document.getElementById("text");

    const onClick = () => {
      axios.get('/translator/test', {  //비동기통신
            text2 : 'text'          
      }).then((response) => {setData(response.data.message.response.translatedText)
      }).catch(error=>{
        console.log(error);
        throw new Error(error);
      });
    };
    return (
        <article>
           //자바에서만 받는 태스임, select는 옵션 파티로 같이 
          <p><input type="text" name='title' placeholder="한글 문장을 입력해 주세요" id="text"></input></p>

        <div>
          <button onClick={onClick}>번역하기</button>
        </div>
        <p><textarea type="msg" name='title' id="msg" value={data}></textarea></p>

      </article>
    );
}

export default App;
